import errorHandler from "./errorHandler";
export default function(name){
    try{
        let matches = document.cookie.match(new RegExp(
            "(?:^|; )" + name.replace(/([\.$?*|{}\(\)\[\]\\\/\+^])/g, '\\$1') + "=([^;]*)"
          ));
          return matches ? decodeURIComponent(matches[1]) : undefined;
    }
    catch(e){
        errorHandler("getCookie", "default", e, "UI")
    }
}
