import check from './SupportThreeCheck.js'
import errorHandler from '../errorHandler.js'
import renderer from './Renderering.js'
let canvas = null;
let ctx;
export default class{
    constructor(){
        try{
            const three = check(canvas);
            canvas = document.querySelector("#canvas");
            if(three != null){
                alert("чекай мать");
            }
            else{
                try{
                    ctx = new renderer(canvas, window.innerWidth, window.innerHeight);
                }
                catch(e){
                    errorHandler('threeLogic', 'constructor_2', e, 'canvas');
                }
            }
        }
        catch(e){
            errorHandler('threeLogic', 'constructor_1', e, 'canvas');
        }
    }
}
